import styles from "./ReviewForm.module.scss";
import { useParams } from "react-router-dom";
import { useRef, Fragment } from "react";
import axios from "axios";

import { useContext, useState } from "react";
import AuthContext from "../../../store/auth-context";

function ReviewForm() {
  const reviewRef = useRef();
  const ratingRef = useRef();
  const [isError, setIsError] = useState();
  const params = useParams();
  const { tripId } = params;
  const authCtx = useContext(AuthContext);
  function submitHandler(e) {
    setIsError(false);
    e.preventDefault();
    if (authCtx.login) {
      const review = reviewRef.current.value;
      const rating = ratingRef.current.value;

      axios.post(`/api/v1/trips/${tripId}/reviews`, { review, rating });
    } else {
      setIsError(true);
    }
  }
  return (
    <Fragment>
      <form onSubmit={submitHandler} className={styles.formReview}>
        <p>Add review</p>
        <div>
          {isError ? <p>Enter valid Data</p> : ""}
          <input className={styles.inputText} ref={reviewRef} type="text" />
          <label>Rating:</label>
          <input className={styles.inputNumber} ref={ratingRef} type="number" />

          <span>
            {" "}
            <input type="radio" name="radio" id="radio1" />
            <label htmlFor="radio1">1</label>
          </span>

          <input type="radio" name="radio" id="radio2" />
          <label htmlFor="radio1">2</label>

          <input type="radio" name="radio" id="radio3" />
          <label htmlFor="radio1">3</label>

          <input type="radio" name="radio" id="radio4" />
          <label htmlFor="radio1">4</label>

          <input type="radio" name="radio" id="radio5" />
          <label htmlFor="radio1">5</label>

          <button>ADD</button>
        </div>
      </form>
    </Fragment>
  );
}

export default ReviewForm;
