import styles from "./ReviewForm.module.scss";
import { useParams } from "react-router-dom";
import { useRef, Fragment } from "react";
import axios from "axios";
import { useCookies } from "react-cookie";

function ReviewForm() {
  const reviewRef = useRef();
  const ratingRef = useRef();
  const [cookies, setCookie] = useCookies();

  const params = useParams();
  const { tripId } = params;

  function submitHandler(e) {
    e.preventDefault();
    const review = reviewRef.current.value;
    const rating = ratingRef.current.value;

    axios.post(`/api/v1/trips/${tripId}/reviews`, { review, rating });
  }
  return (
    <Fragment>
      <p>Add review</p>
      <form onSubmit={submitHandler} className={styles.formReview}>
        <input className={styles.inputText} ref={reviewRef} type="text" />
        <input className={styles.inputNumber} ref={ratingRef} type="number" />
        <button>ADD</button>
      </form>
    </Fragment>
  );
}

export default ReviewForm;
